import './AddPost.css'

const AddPost = (props) => {
    return (
        <div className="AddPost">
            <h1>Add Post</h1>
            <form>
                <label>Id: </label>
                <input type="text"
                    label={'id'}
                    name={'id'}
                    value={props.title} onChange={(event) => setPost({ ...post, title: event.target.value })}
                />
                <label>Title: </label>
                <input type="text"
                    label={'title'}
                    name={'title'}
                    value={props.title}
                />

                <label>Author: </label>
                <input type="text"
                    label={'author'}
                    name={'author'}
                    value={props.author}
                />

                <label>Content: </label>
                <input type="text"
                    label={'content'}
                    name={'content'}
                    value={props.content}
                />

                <button onClick={() => { props.addPost() }}>Add Post </button>

            </form>

        </div>
    )
}

export default AddPost;
